    List<Event> events;
    List<AspNetUsers> users;
    List<Group> groups;
    List<Family> families;
    List<UserFamily> records;
    string userId;
    string familyId;
    string leader;
    string Group;
    string name;
    private enum MODE { None, Add, EditDelete };
    MODE mode = MODE.None;
    Family activity;

    protected async Task Insert()
    {
        familyId = Guid.NewGuid().ToString();

        Family e = new Family()
        {
            FamilyId = familyId,
            LeaderId = leader,
            Name = name
        };
        await familyService.InsertFamilyAsync(e);
        ClearFields();
        await load();
        mode = MODE.None;
    }

    protected void Add()
    {
        //ClearFields();
        mode = MODE.Add;
    }

    protected void ClearFields()
    {
        familyId = string.Empty;
        leader = string.Empty;
        name = string.Empty;
    }

    protected async Task Update()
    {
        Family e = new Family()
        {
            FamilyId = familyId,
            LeaderId = leader,
            Name = name
        };

        await familyService.UpdateFamilyAsync(familyId, e);
        ClearFields();
        await load();
        mode = MODE.None;
    }

    protected async Task Delete()
    {
        await familyService.DeleteFamilyAsync(familyId);
        ClearFields();
        await load();
        mode = MODE.None;
    }

    protected async Task Show(string id)
    {
        activity = await familyService.GetFamilyByIdAsync(id);
        familyId = activity.FamilyId;
        leader = activity.LeaderId;
        name = activity.Name;
        mode = MODE.EditDelete;
    }

    protected override async Task OnInitializedAsync()
    {
        await load();
    }

    protected async Task load()
    {
        users = await aspNetUsersService.GetAspNetUsersAsync();
        families = await familyService.GetFamilyAsync();
        var authState = await AuthenticationStateProvider.GetAuthenticationStateAsync();
        var user = authState.User;
        var currentUser = await UserManager.GetUserAsync(user);
        userId = currentUser.Id;
    }

    async Task LookUp()
    {
        await JsRuntime.InvokeVoidAsync("mySearch");
    }

