    List<Group> groups;
    string groupId;
    string leader;
    string name;
    string description;
    private enum MODE { None, Add, EditDelete };
    MODE mode = MODE.None;
    Group group;

    protected override async Task OnInitializedAsync()
    {
        await load();
    }

    protected async Task load()
    {
        groups = await groupService.GetGroupAsync();
    }

    protected async Task Insert()
    {

        Group g = new Group()
        {
            GroupId = groupId,
            Leader = leader,
            Name = name,
            Description = description
        };

        await groupService.InsertGroupAsync(g);
        ClearFields();
        await load();
        mode = MODE.None;
    }

    protected void ClearFields()
    {


        groupId = string.Empty;
        leader = string.Empty;
        name = string.Empty;
        description = string.Empty;
    }
    protected void Add()
    {
        ClearFields();
        mode = MODE.Add;
    }
    protected async Task Update()
    {

        Group g = new Group()
        {
            GroupId = groupId,
            Leader = leader,
            Name = name,
            Description = description
        };

        await groupService.UpdateGroupAsync(groupId, g);
        ClearFields();
        await load();
        mode = MODE.None;
    }

    protected async Task Delete()
    {
        await groupService.DeleteGroupAsync(groupId);
        ClearFields();
        await load();
        mode = MODE.None;
    }
    protected async Task Show(string id)
    {
        group = await groupService.GetGroupByIdAsync(id);
        groupId = group.GroupId;
        leader = group.Leader;
        description = group.Description;
        name = group.Name;
        mode = MODE.EditDelete;
    }

