    List<AspNetUsers> Users;
    string userId;
    string firstName;
    string lastName;
    string address;
    string state;
    string zip;
    string phone;
    private enum MODE { None, Add, EditDelete };
    MODE mode = MODE.None;
    AspNetUsers User;

    protected override async Task OnInitializedAsync()
    {
        await load();
    }

    protected async Task load()
    {
        Users = await userService.GetAspNetUsersAsync();
    }

    protected void ClearFields()
    {
        firstName = string.Empty;
        lastName = string.Empty;
        address = string.Empty;
        state = string.Empty;
        zip = string.Empty;
        phone = string.Empty;
    }

    protected async Task Update()
    {

        AspNetUsers u = new AspNetUsers()
        {
            FirstName = firstName,
            LastName = lastName,
            Address = address,
            State = state,
            Zip = zip,
            Phone = phone
        };

        await userService.UpdateAspNetUsersAsync(userId, u);
        ClearFields();
        await load();
        mode = MODE.None;
    }

    protected async Task Delete()
    {
        await userService.DeleteAspNetUsersAsync(userId);
        ClearFields();
        await load();
        mode = MODE.None;
    }

    protected async Task Show(string id)
    {
        User = await userService.GetAspNetUsersByIdAsync(id);
        userId = User.Id;
        firstName = User.FirstName;
        lastName = User.LastName;
        address = User.Address;
        state = User.State;
        zip = User.Zip;
        phone = User.Phone;
        mode = MODE.EditDelete;
    }

